    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
        CitoyensDAO cd = new CitoyensDAO();
        if (cintxt.getText().equals("")){
            lmsg.setText("Champ vide !!!!!! veuillez rÃ©sseayer");
        }
        else {
            cd.deletecitoyens(Integer.parseInt(cintxt.getText().toString()));
            JOptionPane.showConfirmDialog(this, "votre profile sera bloquÃ©e ", "Comfirmation", JOptionPane.YES_NO_CANCEL_OPTION);
            JOptionPane.showMessageDialog(this, "votre profile est bloquÃ© ");
            Acceuil1 f = new Acceuil1();
            f.setVisible(true);
            Deletecitoyen.super.setVisible(false);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

