    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        
        Citoyens c=new Citoyens();
       CitoyensDAO cd = new CitoyensDAO();
        String type ="";
        if(FRadioButton2.isSelected()){
            type =FRadioButton2.getText();
        }
        if (HRadioButton1.isSelected()){
            type= HRadioButton1.getText();
        }
        
        String pseudotxt = pseudo.getText();
        int cintxt = Integer.parseInt(cin.getText());
        String nomtxt = nom.getText();
        String prenomtxt= prenom.getText();
        int teltxt = Integer.parseInt(tel.getText());
        String emailtxt= email.getText();
        String mdptxt = pwd.getText();
        Date dateFromDateChooser = dateNaiss.getDate();
       
        java.util.Date date_util = dateNaiss.getDate();
         java.sql.Date date_sql = new java.sql.Date(date_util.getTime());
        //String dateString = String.format("%1$td-%1$tm-%1$tY", dateFromDateChooser);
        c.setLogin(pseudotxt);
        c.setCin(cintxt);
        c.setNom(nomtxt);
        c.setPrenom(prenomtxt);
        c.setSexe(type);
        c.setDateNaissance(date_sql);
        c.setTelephone(teltxt);
        c.setEmail(emailtxt);
        c.setPwd(mdptxt);
        cd.updateCitoyen(c);
        JOptionPane.showMessageDialog(this, "mise Ã  jour avec succÃ©es");
        //SendMail.send();
    }//GEN-LAST:event_jButton2ActionPerformed

